const SearchForm = props => {

  // create a list of treatment-area-icon-buttons
  const treatmentAreaIconButtons = props.treatmentAreaAndIsActivePairs.map( (currArea, i) => {
    const toggleModifier = props.treatmentAreaAndIsActivePairs[i][1] ? '' : ' SearchForm__treatAreaIconBtn--notActive';
    return (
      <li key={currArea + i}>
        <button
          className={"SearchForm__treatAreaIconBtn" + toggleModifier}
          onClick={ () => props.onClick(currArea)}>
          <img             
            src={props.treatAreaIcons[i]} 
            alt={"Icon For " + currArea} />
          <span className="SearchForm__treatmentLabel">{currArea}</span>
        </button>  
      </li>
    );
  });

  return(
    <div className="SearchForm">

      <ul className="SearchForm__treatAreaIconBtnList">
        {treatmentAreaIconButtons}
      </ul>


