// SearchResultsContainer.s

// This container component is responsible for
// processing the raw search results

/* 

  WIP:

*/

import React from "react";

// Import Utils
import {
  addTheTreatmentsOffered, 
  addCountAndNextToEachTreatment, 
  shortenTitleAndDescription, 
  addDistanceToSaloon
} from './Utils/SearchResultsUtils';

import SearchResults from './SearchResults';

const SearchResultsContainer = props => {
  console.log(searchResults);

  let searchResults;

  if (props.rawSearchResults) {

    // deep copy the object
    searchResults = JSON.parse(JSON.stringify(props.rawSearchResults))


    // add the treatment that each saloon offers,
    // to each saloon object
    addTheTreatmentsOffered(searchResults)

    // add a count and next date to each treatment
    addCountAndNextToEachTreatment(searchResults)

    // shorten the title and description, if they are more than 
    // 100 or 200 chars respectively
    shortenTitleAndDescription(searchResults);
  };

  if (props.clientCoordinates && searchResults) {
    addDistanceToSaloon(props.clientCoordinates, searchResults);
  }

  return(
    <SearchResults 
      processedSearchResults={searchResults}
    />
  );
};


export default SearchResultsContainer;
