// SearchFormContainer.js

import React from "react";
import StartSearch from '../components/Search/StartSearch/StartSearch';

// import the icons
import iconHair from '../assets/icons/hair.svg';
import iconFace from '../assets/icons/face.svg';
import iconHand from '../assets/icons/hand.svg';
import iconBody from '../assets/icons/body.svg';
import iconFeet from '../assets/icons/feet.svg';

// create an array of icons for the StartSearch component
const icons = [
  iconHair,
  iconFace,
  iconHand,
  iconBody,
  iconFeet
];

// WARNING: 
// currently it will not work to change the language settings 
let labelsForStartSearch = {
  eng: ['hair', 'face', 'hands', 'body', 'feet'],
  dk: ['Hår', 'Ansigt', 'Hænder', 'Krop', 'Fødder'],
}

// the order of the default values for checked/unchecked matter
// - they match the order of the labels
let defaultValuesForStartSearch = [true, true, true, true, true];

const SearchFormContainer = props => {
  return(
    <StartSearch 
      // pass the def vals for the labels
      defValLabels={labelsForStartSearch.dk}
      // pass the  def vals for the checkboxes (checked/unchecked)
      defValIsChecked={defaultValuesForStartSearch}
      // pass the icons array
      icons={icons}
      // pass along the function used to get search results
      getSearchResults={props.getSearchResults}
    /> 
  );
};


export default SearchFormContainer;
