import React from 'react';
import './MultiSelectList.scss';

const MultiSelectList = props => {
  // let handleClick = (label) => props.onClick(label);
  // loop through the passed list of treatment labels
  // in order to create a list of treatment-check-buttons
  const treatAreaCheckButtons = props.treatAreaAndIsActivePairs.map( (currPair, i) => {
    const label = currPair[0];
    const isActive = currPair[1]; 
    const classNameToggler = props.labelIsActiveList[i] ? " MultiSelectList__item--active" :      ''
    ;
