// TreatmentButtonsList.js

import React from 'react';

const TreatmentButtonsList = props => {

  // Create the treatment-button list items
  const treatmentIconsListItems = props.labels.map( (currentLabel, i) => {
    return (
      // the key is needed when creating lists in react-land
      <li key={currentLabel + i}>
        <button className="treatment-btn">
          <img 
            className=""
            src={props.icons[i]} 
            alt={"Icon For " + currentLabel} />
          <span className="">
            {currentLabel}
          </span>
        </button>  

      </li>
    );
  });

  return(
    <ul className="">
      {treatmentIconsListItems}
    </ul>
  );

};

export default TreatmentButtonsList;
