*/

import React, { useState } from "react";
import "react-modern-calendar-datepicker/lib/DatePicker.css";
import { Calendar } from "react-modern-calendar-datepicker";
import { utils } from 'react-modern-calendar-datepicker';
import './DateRangePicker.css';

// import the custom danish labels
import dkLocale from './dkLocale'

const DateRangePicker = props => {

  // const gregorianToday = utils().getToday(); // { year: 2020, month: 4, day: 3 }

  // const formatInputValue = () => {
  //   if (!selectedDayRange) return '';
  //   return `${selectedDayRange.day}/${selectedDayRange.month}/${selectedDayRange.year}`;
  // };

  // const [selectedDayRange, setSelectedDayRange] = useState({
  //   from: null,
  //   to: null
  // });




  const selectedDayRange = {
    from: props.from,
    to: props.to
  }


  // const handleClick = e => {
  //   console.log(e);

  // }

  // // get the formatted label
  // const test = formatInputValue();

  return (
      <Calendar
        value={selectedDayRange}
        onChange={ e => props.handleDateClick(e) }
        inputPlaceholder="Select a day range"
        locale={dkLocale} // custom locale object
        wrapperClassName="datepicker-wrapper"
        calendarClassName="responsive-calendar" 
        colorPrimary="#959595" 
        colorPrimaryLight="#F7F5F0" 
        calendarTodayClassName="custom-today-day" 
        shouldHighlightWeekends={false}

      />
  );
};

export default DateRangePicker;
