// Nav.js

import React from 'react';
import { Link } from 'react-router-dom';
import Logo from '../Logo/Logo';
import { HamburgerSqueeze as NavBurger } from 'react-animated-burgers'
import NavMenu from './NavMenu/NavMenu';

const Nav = props => {
  // styles for the logo
  const logoStyles = { height: "50px" }

  // the open/close menu toggler
  const menuClassNameToggler = props.isOpen ? " Nav__menu--open" : "";

  return(

    <div
      className="Nav Nav--horizontal">

        {/* compiles to an anchor */}
        <Link
          className="Nav__logoLink"
          to="/"
          onClick={props.handleLogoClick}>
          <Logo style={logoStyles} />
        </Link>

        {/* compiles to a button */}
        <NavBurger
          className="Nav__burger"
          isActive={props.isOpen} 
          barColor="white"
          toggleButton={this.handleClick}
          buttonStyle={{ padding: "0" }}
          buttonWidth={36}
        />

        <div className={"Nav__menu" + menuClassNameToggler}>
          <NavMenu 
            isOpen={props.isOpen}
            onClick={this.handleClick}
          />
        </div>

    </div>
  )
}


export default Nav;
