import React from 'react';
import './Checkbox.css';

const Checkbox = (props) => {
  let handleClick = () => props.onClick(props.label);

  let toggleCheckmark = props.isChecked ? ' displayMark' : '';

  return(
    <div 
      className="checkbox-wrapper" 
      onClick={ () => handleClick() }>
      <span className="checkbox">
        <i className={'checkmark' + toggleCheckmark}></i>
      </span>
      <span className="label">{props.label}</span>
    </div>
  );
};

export default Checkbox;
