// SearchForm.js

import React from 'react';
import Dropdown from './Dropdown/Dropdown'; 
import CalendarDropdown from './CalendarDropdown/CalendarDropdown';

const SearchForm = props => {

  // Create the treatment-icons list items
  const listOfTreatmentButtons = props.labels.map( (currentLabel, i) => {
    return (
      <div 
        className={props.isChecked[i] ? ' active-treatment' : ' inactive-treatment'}
        // the passed function uses the passed label 
        // to identify the treatment btn that has been clicked
        onClick={ () => props.onClick(currentLabel)}
        key={currentLabel + i}
      >
        <button className="treatment-btn">
          <img 
            className="treatment-btn-icon"
            src={props.icons[i]} 
            alt={"Icon For " + currentLabel} />
          <span 
            className="treatment-btn-label">
              {currentLabel}
          </span>
        </button>  

      </div>
    );
  });


  return(
    <div className="SearchForm">

      <div className="SearchForm__treatmentIconsList">
      <ul className="SearchForm__treatmentIconsList"></ul>
        {listOfTreatmentButtons}
      </div>

      {/* Render a Dropdown and pass it some props */}
      <Dropdown 
        labels={props.labels}
        chosenLabels={props.activeLabels}
        isChecked={props.isActiveArray}
        onClick={props.onClick}
      />

      {/* Render the CalendarDropdown component that contains a SingleDatePicker */}
      <CalendarDropdown 
        from={props.fromDate}
        to={props.toDate}
        handleDateClick={props.onDatePick}
      />

      <div 
        className="SubmitButton-wrapper"
        onClick={props.getSearchResults}
        > 
          <span>Submit</span>
      </div>

    </div>
  )
}

export default SearchForm;
