/* 

This component expects an object passed as a property named "data" e.g.:

  {
    heading: 'LINKS',
    links: [
      {
        desc: 'one',
        href: '#'
      },
      {
        desc: 'two',
        href: '#'
      },
    ]
  }

*/

import React from 'react';

const LinksBlock = props => {

  // get the heading
  const heading = props.data.heading;

  // create a list of links
  const list = props.data.links.map((link, i) => {
    return (
      <li 
        className="link-block-list-item"
        /* react requires a unique key when creating lists */
        key={heading + i} 
        >
          <a href={link.href}>
            {link.desc}
          </a>
      </li>
    )
  });

  return(
    <div>
      <h2>{heading}</h2>
      <ul className="link-block-list">
        {list}
      </ul>
    </div>
  );
};

export default LinksBlock;
