// SearchForm.js

// WIP: keep refactoring the creating of the treatments icons list items

import React from 'react';
import DropdownContainer from './Dropdown/DropdownContainer'; 
import MultiSelectList from './MultiSelectList/DropdownContainer'; 
//import CalendarDropdown from './CalendarDropdown/CalendarDropdown';

const SearchForm = props => {

  // Create the treatment-icons list items
  const treatmentIconsListItems = props.labels.map( (currentLabel, i) => {
    const toggleModifier = props.isActiveArray[i] ? '' : ' SearchForm__treatmentIconBtn--inActive';
    return (
      <li 
        className="SearchForm__treatmentIconsListItem" 
        key={currentLabel + i}>
        <button
          className={"SearchForm__treatmentIconBtn" + toggleModifier}
          onClick={ () => props.onClick(currentLabel)}>
          <img 
            className="SearchForm__treatmentIcon"
            src={props.icons[i]} 
            alt={"Icon For " + currentLabel} />
          <span className="SearchForm__treatmentLabel">{currentLabel}</span>
        </button>  
      </li>
    );
  });

  return(
    <div className="SearchForm">

      <ul className="SearchForm__treatmentIconsList">
        {treatmentIconsListItems}
      </ul>


      <div className="SearchForm__treatmentDrowdown">
        <DropdownContainer 
          label={"test label"}
          body={
            <MultiSelectList 
              labelList={treatmentLabels.dk}
              // note in the real app, the list of labels is referenced via this.labels not the treatmentLabels object
              labelIsActiveList={labelIsActiveList}
              onClick={clickHandler}
            />
          }
        />

        {/* <Dropdown 
          label={props.labels}
          activeabels={props.activeLabels}
          isChecked={props.isActiveArray}
          onClick={props.onClick}
        /> */}
      </div>

      {/* Render the CalendarDropdown component that contains a SingleDatePicker */}
{/*       <CalendarDropdown 
        from={props.fromDate}
