// SearchFormContainer.js

import React, { Component } from "react";
import SearchForm from './SearchForm';

// Import the icons
import iconHair from '../../../assets/icons/hair.svg';
import iconFace from '../../../assets/icons/face.svg';
import iconHand from '../../../assets/icons/hand.svg';
import iconBody from '../../../assets/icons/body.svg';
import iconFeet from '../../../assets/icons/feet.svg';

// Icons paths
const icons = [ iconHair, iconFace, iconHand, iconBody, iconFeet ];

// WARNING: currently it will not work to change the language settings 
let treatmentAreas = {
  eng: ['Hair', 'Face', 'Hands', 'Body', 'Feet'],
  dk: ['Hår', 'Ansigt', 'Hænder', 'Krop', 'Fødder'],
}

// warning: it will not work to set a different locale (for now)
// but I am enforcing this idea througout the code, for the future
let locale = 'dk';

// These are default values. Note: the order matters -
// it matches the order of the treatments labels arr
// many months later: but why? seems confusing to declare this array out of context, 
// why dont we create an object with the values instead?
// let treatmentLabelIsActive = [true, true, true, true, true]; // remove after refactoring
// why not do something like this instead? its less error prone, 
// and way more meaningful just by looking at it! 


const labelData = {
  hair: {
    label: treatmentLabels[locale][0],
    isActive: true,
    icon: icons[0],
  },
  face: {
    label: treatmentLabels[locale][1],
    isActive: true,
    icon: icons[1],
  },
  hands: {
    label: treatmentLabels[locale][2],
    isActive: true,
    icon: icons[2],
  },
  body: {
    label: treatmentLabels[locale][3],
    isActive: true,
    icon: icons[3],
  },
  feet: {
    label: treatmentLabels[locale][4],
    isActive: true,
    icon: icons[4],
  },
}

console.log('PRINT STUFF:');
console.log(labelData);

class SearchFormContainer extends Component {
  constructor(props) {
    super(props);

    // Set the state
    this.state = {
      // set the default values 
      [labelData['hair'].label]:  labelData['hair'].isActive,
      [labelData['face'].label]:  labelData['face'].isActive,
      [labelData['hands'].label]: labelData['hands'].isActive,
      [labelData['body'].label]:  labelData['body'].isActive,
      [labelData['feet'].label]:  labelData['feet'].isActive,
      from: null,
      to: null, 
    }

    // Create a list of labels
    this.labels = Object.keys(this.state).splice(0, 5);

    // 

    // Binders
    this.handleClick = this.handleClick.bind(this);
    this.handleDateClick = this.handleDateClick.bind(this);
    this.handleSubmitClick = this.handleSubmitClick.bind(this);
  }

  // this handler is triggered when you click on a treatment-icon
  // and when you click on a treatment-checkbox
  handleClick(clickedTreatment) {
    this.setState({ [clickedTreatment]: !this.state[clickedTreatment] });
  }

  handleDateClick(e) {
    this.setState({
      from: e.from,
      to: e.to
    });
  }

  handleSubmitClick() {
    this.props.getSearchResults(this.state); 
  }

  render() {

    // get the checked/unchecked array
    let treatmentIsActiveArr = Object.values(this.state).slice(0,5)

    // Get a list of the active treatments for the Dropdown
    let activeTreatments = formatActiveTreatments(getActiveTreatmentsList(this.state));

    return(
      <SearchForm

        // props for the treatmentIconButtonList

        // pass the labels array
        labels={this.labels}
        activeLabels={activeTreatments}
        isActiveArray={treatmentIsActiveArr}

        // pass the list of icon paths
        icons={icons}


        onClick={this.handleClick}

        // pass the method used to get search results
        getSearchResults={this.handleSubmitClick}

        // props for DatePicker
        fromDate={this.state.from} 
        toDate={this.state.to}
        onDatePick={this.handleDateClick} 
      />
    )
  }
}

export default SearchFormContainer;





/* 

  Helper Functions

*/


const getActiveTreatmentsList = obj => {
  // get an array of the components state key/value pairs,
  // this will result in an array of nested arrays
  // and remove the 'from' and 'to' from the array
  let objectsKeyValPairsArr = Object.entries(obj).slice(0,5)

  // initiate an empty array that will store the selected labels only
  const selectedLabelsArray = [];

  // loop through the objectsKeyValPairsArr (its an array of arrays)
  objectsKeyValPairsArr.forEach( (currentElement, i) => {
    // in the current element ([<label>],[<isChecked>]),
    // if isChecked is true   
    if(currentElement[1]) {
      // then push the label to the selectedLabelsArray
      selectedLabelsArray.push(currentElement[0])
    };
  });

  return selectedLabelsArray;
};

// helper function, formatActiveTreatments,
// takes an array contain the selected labels
// and returns a new array with the formatted labels
// for the dropdown component
const formatActiveTreatments = arr => {
  // create a new array where each element has a space before the string,
  // if not used, the result is: <label>,<label>,<label>
  let formattedLabels = arr.map( element => " " + element);

  return formattedLabels;
};
