// Nav.js

import React from 'react';
import NavBar from './NavBar/NavBar';
import NavMenu from './NavMenu/NavMenu';

class Nav extends React.Component {
  constructor(props) {
    super(props);
    // default state for the menu
    this.state = { isOpen: false }

    // binders
    this.handleClick = this.handleClick.bind(this);
    this.handleOutsideClick = this.handleOutsideClick.bind(this);
    this.handleLogoClick = this.handleLogoClick.bind(this);
  }

  handleLogoClick() {
    // if the menu is open, call handleClick
    if (this.state.isOpen) { this.handleClick(); }
  }

  handleClick() {
    // before toggling the isOpen state, check if the menu is open/closed
    // in order to add/remove the eventhandler that is responsible for 
    // checking if users clicks outside/inside of the nav bar and menu
    // so that we can close the menu if users clicks outside of nav
    if (!this.state.isOpen) {
      document.addEventListener('click', this.handleOutsideClick, false);
    } else {
      document.removeEventListener('click', this.handleOutsideClick, false);
    }
    // toggle the state, which in turn closes/opens the menu
    this.setState({ isOpen: !this.state.isOpen })
  }

  handleOutsideClick(e) {
    // ignore clicks on the component itself
    if (this.node.contains(e.target)) { return; }
    // otherwise call handleClick, that will remove the handler, 
    // and close the menu
    this.handleClick();
  }

  render() {
    return(
      <div 
        className="Nav"
        // the following reference is needed to track clicks outside/inside of Nav
        // in order to close the menu, once its open  
        ref={node => { this.node = node; }}>
          <NavBar
            isOpen={this.state.isOpen} 
            onBurgerClick={this.handleClick}
            onLogoClick={this.handleLogoClick}
          />
          <NavMenu 
            isOpen={this.state.isOpen}

          />
      </div>
    )
  }
}

export default Nav;
