import React from "react";
import SaloonCardContainer from './SaloonCard/SaloonCardContainer';
import Treatment from './Treatment/Treatment';
import './SearchResults.css';

const SearchResults = props => {
  let saloons = props.processedSearchResults;

  let output = [];

  if (saloons) {
    // Loop through the saloons
    for (let i = 0; i < saloons.length; i++) {
      const saloon = saloons[i];

      // This variable will house the treatments list
      let treatmentsList = [];

      // Create the list of treatments offered
      for (let j = 0; j < saloon.treatmentsOffered.length; j++) {
        const treatment = saloon.treatmentsOffered[j];

        // Create one treatment component and push i the treatments list
        treatmentsList.push(
          <Treatment
            treatment={treatment}
            key={'treatmentKey' + j}
          />
        );

        // end of creating the treatments list
      };

      // Create the saloon card
      output.push(
        <SaloonCardContainer 
          saloon={saloon}
          treatmentsList={treatmentsList}
          key={'saloonKey' + i}
        />
      );

      // Reset the treatments list, before starting a new loop, 
      // and creating a new saloon card
      treatmentsList = [];
    };
  };

  return(
    <div className="search-results-wrapper">
      {output}
    </div>
  );
};

export default SearchResults;
