// SearchFormContainer.js

import React, { Component } from "react";
import SearchForm from './SearchForm';

// Import the icons
import iconHair from '../../../assets/icons/hair.svg';
import iconFace from '../../../assets/icons/face.svg';
import iconHand from '../../../assets/icons/hand.svg';
import iconBody from '../../../assets/icons/body.svg';
import iconFeet from '../../../assets/icons/feet.svg';

// Create an object for the labels in different languages
let treatmentAreas = {
  eng: ['Hair', 'Face', 'Hands', 'Body', 'Feet'],
  dk: ['Hår', 'Ansigt', 'Hænder', 'Krop', 'Fødder'],
}

// WARNING: currently it will not work to change the language settings 
let locale = 'dk';

// Create an object for the treatmentAreaData
const treatmentAreaData = {
  hair: {
    label: treatmentAreas[locale][0],
    isActive: true,
    icon: iconHair,
  },
  face: {
    label: treatmentAreas[locale][1],
    isActive: true,
    icon: iconFace,
  },
  hands: {
    label: treatmentAreas[locale][2],
    isActive: true,
    icon: iconHand,
  },
  body: {
    label: treatmentAreas[locale][3],
    isActive: true,
    icon: iconBody,
  },
  feet: {
    label: treatmentAreas[locale][4],
    isActive: true,
    icon: iconFeet,
  },
}

class SearchFormContainer extends Component {
  constructor(props) {
    super(props);

    // Set the initial default values
    this.state = {
      [treatmentAreaData['hair'].label]:  treatmentAreaData['hair'].isActive,
      [treatmentAreaData['face'].label]:  treatmentAreaData['face'].isActive,
      [treatmentAreaData['hands'].label]: treatmentAreaData['hands'].isActive,
      [treatmentAreaData['body'].label]:  treatmentAreaData['body'].isActive,
      [treatmentAreaData['feet'].label]:  treatmentAreaData['feet'].isActive,
      from: null,
      to: null, 
    }

    // Create a list of icons. Note: we are looping through the treatmentAreaData-obj, 
    // NOT the state object, because these vals will not change
    this.treatmentAreaIconList = Object.entries(treatmentAreaData).map( currEl => currEl[1].icon);

    // Binders
    this.handleClick = this.handleClick.bind(this);
    this.handleDateClick = this.handleDateClick.bind(this);
    this.handleSubmitClick = this.handleSubmitClick.bind(this);
  }

  // toggle the clicked treatment-area isActive value
  handleClick(clickedTreatmentArea) {
    this.setState({ [clickedTreatmentArea]: !this.state[clickedTreatmentArea] });
  }

  handleDateClick(e) {
    this.setState({
      from: e.from,
      to: e.to
    });
  }

  handleSubmitClick() {
    this.props.getSearchResults(this.state); 
  }

  render() {
    console.log('hello');
    // Create a list of entries from this state
    // corrsponding to the treatmentAreas-keys and their isActive-values
    // e.g [['Hair', true],['Face', true]]
    let treatmentAreasEntries = Object.entries(this.state).slice(0,5);

    let test = getActiveTreatmentAreasList(treatmentAreasEntries);

    // Get a list of the active treatments for the Dropdown
    //let activeTreatments = formatActiveTreatments(getActiveTreatmentsList(this.state));

    return(
      <SearchForm
        // pass the list of treatmentArea and isActive pairs

        treatAreaAndIsActivePairs={treatmentAreasEntries}

        // treatmentAreasIsActive={treatmentAreasIsActiveList} 

        treatAreaIcons={this.treatmentAreaIconList}

        activeTreatAreas={test}

        // props for Dropdown
        //activeLabels={activeTreatments}


        // pass the list of icon paths


        onClick={this.handleClick}

        // pass the method used to get search results
        getSearchResults={this.handleSubmitClick}

        // props for DatePicker
        fromDate={this.state.from} 
        toDate={this.state.to}
        onDatePick={this.handleDateClick} 
      />
    )
  }
}

export default SearchFormContainer;





/* 

  Helper Functions

*/

// refacor the function
// currently: it takes an object, but it should beable to take a list of nested arrays
// the list of entries... 

const getActiveTreatmentAreasList = entries => {
  // takes a list of entries: e.g [['Hair', true],['Face', false],['Hands', true]]
  // returns a list of active treatment areas only:  e.g. ['Hair', 'Hands']

  // loop through the array and remove all inactive pairs
  let activePairsOnly = entries.filter( currEl => currEl[1] === true )
  console.log('activePairsOnly:')
  console.log(activePairsOnly)

  let activeTreatmentAreas = activePairsOnly.map(currEl => " " + currEl[0]);
  console.log('activeTreatmentAreas:')
  console.log(activeTreatmentAreas)
  return activeTreatmentAreas
}

const formatActiveTreatmentAreaLabels = arr => {
  // takes an array of active labels: ['Hair,'']
  // create a new array where each element has a space before the string,
  // if not used, the result is: <label>,<label>,<label>
  let formattedLabels = arr.map( element => " " + element);

  return formattedLabels;
};
