// SearchForm.js

// WIP: keep refactoring the creating of the treatments icons list items

import React from 'react';
import Dropdown from './Dropdown/Dropdown'; 
import CalendarDropdown from './CalendarDropdown/CalendarDropdown';

const SearchForm = props => {

  // Create the treatment-icons list items
  const treatmentIconsListItems = props.labels.map( (currentLabel, i) => {
    const isActiveToggler = props.isActiveArray[i] ? ' active-treatment' : ' inactive-treatment';

    return (
      <li key={currentLabel + i}>
        <button
          className={"SearchForm__treatmentIconsList" + isActiveToggler}
          // the onClick function uses the label to idenfity the btn clicked
          onClick={ () => props.onClick(currentLabel)}>
          <img 
            className="treatment-btn-icon"
            src={props.icons[i]} 
            alt={"Icon For " + currentLabel} />
          <span className="treatment-btn-label">{currentLabel}</span>
        </button>  
      </li>
    );
  });


  return(
    <div className="SearchForm">

      <ul className="SearchForm__treatmentIconsList">
        {treatmentIconsListItems}
      </ul>

      {/* Render a Dropdown and pass it some props */}
      <Dropdown 
        labels={props.labels}
        chosenLabels={props.activeLabels}
        isChecked={props.isActiveArray}
        onClick={props.onClick}
      />

      {/* Render the CalendarDropdown component that contains a SingleDatePicker */}
      <CalendarDropdown 
        from={props.fromDate}
        to={props.toDate}
        handleDateClick={props.onDatePick}
      />

      <div 
        className="SubmitButton-wrapper"
        onClick={props.getSearchResults}
        > 
          <span>Submit</span>
      </div>

    </div>
  )
}

export default SearchForm;
