// SearchForm.js

import React from 'react';

const SearchForm = props => {
  // #refactor!
    // get the boolean values from the this.state object
    // that will be passed to the children components
    // and slice to remove the from and date values (this is not the most ideal solution
    // because the slice will run everytime the render function runs, it would be better 
    // to handle this in the constructor, by setting two object in the state - one for 
    // the treatment and labels, and another for the dates )
    let checkedUncheckedValuesArrray = Object.values(this.state).slice(0,5)

    // get an array contain only the selected formatted labels
    // that will be passed to the Dropwdown component
    // and used by the component to output the chosen treatment(s)
    let chosenLabels = formatSelectedLabels(getSelectedLabels(this.state));

  return(
    <div className="StartSearch-wrapper">

      {/* Render a TreatmentButtonsList and pass it some props */}
      <TreatmentButtonsList
        labels={this.labels}
        isChecked={checkedUncheckedValuesArrray}
        onClick={this.handleClick}
        icons={this.props.icons}
      />

      {/* Render a Dropdown and pass it some props */}
      <Dropdown 
        labels={this.labels}
        chosenLabels={chosenLabels}
        isChecked={checkedUncheckedValuesArrray}
        onClick={this.handleClick}
      />

      {/* Render the CalendarDropdown component that contains a SingleDatePicker */}
      <CalendarDropdown 
        from={this.state.from}
        to={this.state.to}
        handleDateClick={this.handleDateClick}
      />

      {/* Submit Button */}
      <SubmitButton 
        handleClick={this.handleSubmitClick}
      />
    </div>
  )
}

export default SearchForm;
