// SearchFormAndResultContainer.js

import React from 'react';
import GetSearchString from '../components/Search/GetSearchString/GetSearchString';
import SearchFormContainer from './SearchFormContainer';

class SearchFormAndResultContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchResults: null,
      lat: null,
      lon: null,
    };
    this.getSearchResults = this.getSearchResults.bind(this);
  };

  componentDidMount() {
    if (navigator.geolocation) {
      // the arrow-success-func is a required func for the 
      // getCurrentPosition method and takes a position object only
      navigator.geolocation.getCurrentPosition( (position) => {
        this.setState({
          lat: position.coords.latitude,
          lon: position.coords.longitude
        });
      });
    };
  };

  getSearchResults(searchParams) {
    // Get a formatted string for the api from the passed search params
    let stringForApi = GetSearchString(searchParams);

    fetch(stringForApi)
      .then(res => res.json())
      .then((data) => {
        this.setState({ searchResults: data });
      })
      // Log any errors to the console
      .catch(console.log)
  };

  render() {
    return(
      <div className="SearchFormAndResultWrapper">

          <div className="Search">
            <h2 className="Search__title">Find behandling</h2>
            <div className="Search__form">
              <SearchFormContainer 
                getSearchResults={this.getSearchResults}
              />
            </div>
            <div className="Search__image">
              Search__image
            </div>
          </div>

          <div className="Search">
            <div className="Result_cards">
              {/* <ResultsContainer /> */}
              <SearchResultsContainer />
            </div>
          </div>

      </div>
    );
  };
};

export default SearchFormAndResultContainer;
