// SearchForm.js

import React from 'react';
import DropdownContainer from './Dropdown/DropdownContainer'; 
import MultiSelectList from './MultiSelectList/MultiSelectList'; 
//import CalendarDropdown from './CalendarDropdown/CalendarDropdown';

const SearchForm = props => {

  // create a list of treatment-area-icon-buttons
  const treatmentAreaIconButtons = props.treatmentAreaLabelList.map( (currentLabel, i) => {
    const toggleModifier = props.treatmentAreaIsActiveList[i] ? '' : ' SearchForm__treatmentIconBtn--notActive';
    return (
      <li key={currentLabel + i}>
        <button
          className={"SearchForm__treatmentIconBtn" + toggleModifier}
          className={"SearchForm__treatAreaIconBtn" + toggleModifier}
          onClick={ () => props.onClick(currentLabel)}>
          <img 
            className="SearchForm__treatmentIcon"
            src={props.icons[i]} 
            alt={"Icon For " + currentLabel} />
          <span className="SearchForm__treatmentLabel">{currentLabel}</span>
        </button>  
      </li>
    );
  });

  return(
    <div className="SearchForm">

      <ul className="SearchForm__treatAreaIconBtnList">
        {treatmentAreaIconButtons}
      </ul>


      <div className="SearchForm__treatmentDrowdown">
        <DropdownContainer 
          label={props.activeLabels}
          body={
            <MultiSelectList 
              labelList={props.treatmentAreaLabelList}
              labelIsActiveList={props.activeLabels}
              onClick={props.onClick}
            />
          }
        />

        {/* <Dropdown 
          label={props.treatmentAreaLabelList}
          activeabels={props.activeLabels}
          isChecked={props.isActiveArray}
          onClick={props.onClick}
        /> */}
      </div>

      {/* Render the CalendarDropdown component that contains a SingleDatePicker */}
{/*       <CalendarDropdown 
        from={props.fromDate}
        to={props.toDate}
        handleDateClick={props.onDatePick}
      />

      <div 
        className="SubmitButton-wrapper"
        onClick={props.getSearchResults}> 
          <span>Submit</span>
      </div> */}

    </div>
  )
}

export default SearchForm;
