import React from 'react';
import './TreatmentButton.css';

const TreatmentButton = props => {  

  // default is true
  let activeOrInactive = props.isChecked ? ' active-treatment' : ' inactive-treatment'

  return(
    <div  
      className={"treatment-btn-wrapper" + activeOrInactive}
      // the passed function uses the passed label 
      // to identify the treatment btn that has been clicked
      onClick={ () => props.onClick(props.label)}
      >   
        <button className="treatment-btn">
          <img 
            className="treatment-btn-icon"
            src={props.icon} 
            alt={"Icon For " + props.label} />
          <span 
            className="treatment-btn-label">
              {props.label}
          </span>
        </button>
    </div>
  );
};

export default TreatmentButton;
